import React, { useState, useEffect } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtn } from "../components/Form";
import pic1 from "./dataproduction.png";
import emailjs from "emailjs-com";

function Dataproduction() {
  // Setting our component's initial state
  const [books, setBooks] = useState([])
  const [formObject, setFormObject] = useState({})

  // Load all books and store them with setBooks
  useEffect(() => {
    loadBooks()
  }, [])

  // Loads all books and sets them to books
  function loadBooks() {
    API.getBooks()
      .then(res => 
        setBooks(res.data)
      )
      .catch(err => console.log(err));
  };

  // Deletes a book from the database with a given id, then reloads books from the db
  function deleteBook(id) {
    API.deleteBook(id)
      .then(res => loadBooks())
      .catch(err => console.log(err));
  }

  // Handles updating component state when the user types into the input field
  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({...formObject, [name]: value})
  };

  // When the form is submitted, use the API.saveBook method to save the book data
  // Then reload books from the database
  function handleFormSubmit(event) {

    event.preventDefault();
    if (formObject.title && formObject.author) {
      API.saveBook({
        title: formObject.title,
        author: formObject.author,
        synopsis: formObject.synopsis
      })
        .then(alert("FORM SUBMITTED, A SPECIALIST WILL REACH OUT TO YOU VIA EMAIL SOON!!! THANK YOU FOR BOOKING WITH GLOBAL INTEGRITY!!!"))
        .catch(err => console.log(err));
    }
    emailfunction();
  };
  function emailfunction() {

  var templateParams = {

  }

    emailjs.send('default_service', 'template_dhpghJPo', templateParams, "user_Z1HryPA1jzVwmj7gx0knZ")
    .then(function(response) {
       console.log('SUCCESS!', response.status, response.text);
    }, function(error) {
       console.log('FAILED...', error);
    })
  }

    return (
      <Container fluid>
        <Row>
          <Col size="lg-12" >


            <div >
              <h1 style={{width: 500, marginLeft: 490, fontSize: 60}}><strong>DATA PRODUCTION</strong></h1>
           </div>

           </Col>
           </Row>
        <Row>
          <Col size="lg-12" >


            <div >
              <hr></hr> <img style={{width: 500, marginLeft: 450}} src={pic1} alt="a man" /><hr></hr>
           </div>
           </Col>
           </Row>
           <Row>
          <Col size="lg-12" >


            <div >
              <h1 style={{width: 500, marginLeft: 450, fontSize: 20}}>Save time by allowing us to acquire customer lead contact information for you. In a world filled with data, which is the most valuable? We will work to compile and refine some of the strongest purchase lists of leads available to you. Our specialists ensure that only the most valuable data passes on to our partners by using a series of guidelines that requires strict vetting of acquired contact information. Contact a specialist today to receive a quote.</h1>
              <br></br>
           </div>

           </Col>
           </Row>


        <Row>
          <Col size="md-6">



            <Jumbotron>
              <h3>Fill out and submit your contact information below and we will reach out to you within one business day to schedule your FREE consultation</h3>
            <form>
              <Input
                onChange={handleInputChange}
                name="title"
                placeholder="Name (required)"
              />
              <Input
                onChange={handleInputChange}
                name="author"
                placeholder="Email Address (required)"
              />
              <TextArea
                onChange={handleInputChange}
                name="synopsis"
                placeholder="Message (Optional)"
              />
              <br></br>
              <FormBtn
                disabled={!(formObject.author && formObject.title)}
                onClick={handleFormSubmit}

              >
                Submit Booking Request
              </FormBtn>
            </form>
            </Jumbotron>
            <br></br>
          </Col>

        </Row>
      </Container>
    );
  }


export default Dataproduction;
