import React, { useState, useEffect } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtn } from "../components/Form";
import pic1 from "./guy.jpg";

function Books() {
  // Setting our component's initial state
  const [books, setBooks] = useState([])
  const [formObject, setFormObject] = useState({})

  // Load all books and store them with setBooks
  useEffect(() => {
    loadBooks()
  }, [])

  // Loads all books and sets them to books
  function loadBooks() {
    API.getBooks()
      .then(res => 
        setBooks(res.data)
      )
      .catch(err => console.log(err));
  };

  // Deletes a book from the database with a given id, then reloads books from the db
  function deleteBook(id) {
    API.deleteBook(id)
      .then(res => loadBooks())
      .catch(err => console.log(err));
  }

  // Handles updating component state when the user types into the input field
  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({...formObject, [name]: value})
  };

  // When the form is submitted, use the API.saveBook method to save the book data
  // Then reload books from the database
  function handleFormSubmit(event) {
    event.preventDefault();
    if (formObject.title && formObject.author) {
      API.saveBook({
        title: formObject.title,
        author: formObject.author,
        synopsis: formObject.synopsis
      })
        .then(res => loadBooks())
        .catch(err => console.log(err));
    }
  };

    return (
      <Container fluid>
        <Row>
          <Col size="lg-12">
            <Jumbotron>
              <h1>Join Our Professional Network!</h1>
            </Jumbotron>

            <div>
              <hr></hr> <img src={pic1} alt="a man" />
            <img src={pic1} alt="a man" />
            <img src={pic1} alt="a man" /></div>
            <hr>
            </hr>
            <Jumbotron>
              <p>Global Integrity will assist you in making an income through the digital market immediately. We will show you the quickest methods to begin earning as a web-based contractor.</p>
<p>In this program you will be presented the opportunity to:</p>
<p>Create your own company</p>
<p>Contract jobs with Global Integrity Marketing</p>
<p>Begin or develop your involvement in the cryptocurrency market (bitcoin)</p>
<p>Develop an asset portfolio</p>
<p>And more</p>
</Jumbotron>
          </Col>
        </Row>
        <Row>
          <Col size="md-6">
            <Jumbotron>
              <h1>Book Our Services Today!</h1>
              <img></img>
            </Jumbotron>
            <Jumbotron>
              <h3>Fill out and submit your contact information below and we will reach out to you within one business day to schedule your FREE consultation</h3>
            <form>
              <Input
                onChange={handleInputChange}
                name="title"
                placeholder="Name (required)"
              />
              <Input
                onChange={handleInputChange}
                name="author"
                placeholder="Email Address (required)"
              />
              <TextArea
                onChange={handleInputChange}
                name="synopsis"
                placeholder="Message (Optional)"
              />
              <FormBtn
                disabled={!(formObject.author && formObject.title)}
                onClick={handleFormSubmit}
              >
                Submit Booking Request
              </FormBtn>
            </form>
            </Jumbotron>

          </Col>
          <Col size="md-6 sm-12">
            <Jumbotron>
              <h1>My Booking Requests</h1>
            {books.length ? (
              <List>
                {books.map(book => (
                  <ListItem key={book._id}>
                    <Link to={"/books/" + book._id}>
                      <strong>
                        {book.title} has requested a booking
                      </strong>
                    </Link>
                    <DeleteBtn onClick={() => deleteBook(book._id)} />
                    <hr></hr>
                  </ListItem>
                ))}
              </List>
            ) : (
              <h3>No Results to Display</h3>
            )}
            </Jumbotron>
          </Col>
        </Row>
      </Container>
    );
  }


export default Books;
