import React, { useState, useEffect } from "react";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Col, Row, Container } from "../components/Grid";
import { Input, TextArea, FormBtn } from "../components/Form";
import pic1 from "./consultation.png";
import emailjs from "emailjs-com";


function Consultation() {
  // Setting our component's initial state
  const [bookings, setBookings] = useState([])
  const [formObject, setFormObject] = useState({})

  // Load all bookings and store them with setBookings
  useEffect(() => {
    loadBookings()
  }, [])

  // Loads all bookings and sets them to bookings
  function loadBookings() {
    API.getBookings()
      .then(res => 
        setBookings(res.data)
      )
      .catch(err => console.log(err));
  };

  // Deletes a booking from the database with a given id, then reloads bookings from the db
  function deleteBooking(id) {
    API.deleteBooking(id)
      .then(res => loadBookings())
      .catch(err => console.log(err));
  }

  // Handles updating component state when the user types into the input field
  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({...formObject, [name]: value})
  };

  // When the form is submitted, use the API.saveBooking method to save the booking data
  // Then reload bookings from the database
  function handleFormSubmit(event) {

    event.preventDefault();
    if (formObject.title && formObject.author) {
      API.saveBooking({
        title: formObject.title,
        author: formObject.author,
        synopsis: formObject.synopsis
      })
        .then(alert("FORM SUBMITTED, A SPECIALIST WILL REACH OUT TO YOU VIA EMAIL SOON!!! THANK YOU FOR BOOKING WITH GLOBAL INTEGRITY!!!"))
        .catch(err => console.log(err));
    }
    emailfunction();
  };
  function emailfunction() {

  var templateParams = {

  }

    emailjs.send('default_service', 'template_dhpghJPo', templateParams, "user_Z1HryPA1jzVwmj7gx0knZ")
    .then(function(response) {
       console.log('SUCCESS!', response.status, response.text);
    }, function(error) {
       console.log('FAILED...', error);
    })
  }

    return (
      <Container fluid>
        <Row>
          <Col size="lg-12" >


            <div >
              <h1 style={{width: 500, marginLeft: 490, fontSize: 60}}><strong>CONSULTATION</strong></h1>
           </div>

           </Col>
           </Row>
        <Row>
          <Col size="lg-12" >


            <div >
              <hr></hr> <img style={{width: 500, marginLeft: 450}} src={pic1} alt="a man" /><hr></hr>
           </div>
           </Col>
           </Row>
           <Row>
          <Col size="lg-12" >


            <div >
              <h1 style={{width: 500, marginLeft: 450, fontSize: 20}}>Begin creating the roadmap to your success in as little as 30 minutes with our free consultation services. We offer free in-person consultation services in the Austin area. Located outside of Austin, TX? Take advantage of our quick and easy web-based national consultation service. Submit a request and one of our account managers will be in touch with you by the next business day. Be sure to arrive prepared with as much information and as many questions as possible to receive the most out of your appointment.</h1>
              <br></br>
           </div>

           </Col>
           </Row>

        <Row>
          <Col size="md-6">



            <Jumbotron>
              <h3>Fill out and submit your contact information below and we will reach out to you within one business day to schedule your FREE consultation</h3>
            <form>
              <Input
                onChange={handleInputChange}
                name="title"
                placeholder="Name (required)"
              />
              <Input
                onChange={handleInputChange}
                name="author"
                placeholder="Email Address (required)"
              />
              <TextArea
                onChange={handleInputChange}
                name="synopsis"
                placeholder="Message (Optional)"
              />
              <br></br>
              <FormBtn
                disabled={!(formObject.author && formObject.title)}
                onClick={handleFormSubmit}

              >
                Submit Booking Request
              </FormBtn>
            </form>
            </Jumbotron>
            <br></br>
          </Col>

        </Row>
      </Container>
    );
  }


export default Consultation;
