import React, { useState, useEffect } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtn } from "../components/Form";
import pic1 from "./cataloging.png";


function Cataloging() {
  // Setting our component's initial state
  const [books, setBooks] = useState([])
  const [formObject, setFormObject] = useState({})

  // Load all books and store them with setBooks
  useEffect(() => {
    loadBooks()
  }, [])

  // Loads all books and sets them to books
  function loadBooks() {
    API.getBooks()
      .then(res => 
        setBooks(res.data)
      )
      .catch(err => console.log(err));
  };

  // Deletes a book from the database with a given id, then reloads books from the db
  function deleteBook(id) {
    API.deleteBook(id)
      .then(res => loadBooks())
      .catch(err => console.log(err));
  }

  // Handles updating component state when the user types into the input field
  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({...formObject, [name]: value})
  };

  // When the form is submitted, use the API.saveBook method to save the book data
  // Then reload books from the database
  function handleFormSubmit(event) {
    event.preventDefault();
    if (formObject.title && formObject.author) {
      API.saveBook({
        title: formObject.title,
        author: formObject.author,
        synopsis: formObject.synopsis
      })
        .then(res => loadBooks())
        .catch(err => console.log(err));
    }
  };

    return (
      <Container fluid>
          <Row>
          <Col size="lg-12" >


            <div >
              <h1 style={{width: 500, marginLeft: 530, fontSize: 60}}><strong>CATALOGING</strong></h1>
           </div>

           </Col>
           </Row>
        <Row>
          <Col size="lg-12" >


            <div >
              <hr></hr> <img style={{width: 500, marginLeft: 450}} src={pic1} alt="a man" />
           </div>
           </Col>
           </Row>

        <Row>
          <Col size="md-6">



            <Jumbotron>
              <h3>Fill out and submit your contact information below and we will reach out to you within one business day to schedule your FREE consultation</h3>
            <form>
              <Input
                onChange={handleInputChange}
                name="title"
                placeholder="Name (required)"
              />
              <Input
                onChange={handleInputChange}
                name="author"
                placeholder="Email Address (required)"
              />
              <TextArea
                onChange={handleInputChange}
                name="synopsis"
                placeholder="Message (Optional)"
              />
              <br></br>
              <FormBtn
                disabled={!(formObject.author && formObject.title)}
                onClick={handleFormSubmit}

              >
                Submit Booking Request
              </FormBtn>
            </form>
            </Jumbotron>
            <br></br>
          </Col>
          <Col size="md-6 sm-12">

              <h1>My Booking Requests</h1>
            {books.length ? (
              <List>
                {books.map(book => (
                  <ListItem key={book._id}>
                    <Link to={"/books/" + book._id }>
                      <strong>
                        You requested a booking on {book.date}. To cancel, simply click the red 'X' to the right.
                      </strong>
                    </Link>
                    <DeleteBtn onClick={() => deleteBook(book._id)} />
                    <br></br>
                    <br></br>
                  </ListItem>
                ))}
              </List>
            ) : (
              <h3>No Results to Display</h3>
            )}

          </Col>
        </Row>
      </Container>
    );
  }


export default Cataloging;
