import React, { useState } from "react";
import Calendar from "react-calendar";
import Jumbotron from "../components/Jumbotron";

const Events = () => {
  const [date, setDate] = useState(new Date());

  const onChange = date => {
      setDate(date)
  };
  const tileContent = ({ date, view }) => view === 'month' && date.getDay() === 1 ? <p>Marketing Class</p> : null;
  const tileContento = ({ date, view }) => view === 'month' && date.getDay() === 3 ? <p>Bitcoin 101</p> : null;
  return <div>
    <Jumbotron>
      <h1 style={{fontSize: 24}}><b>CHECK OUT OUR EVENT CALENDAR</b></h1>
      <br></br>
      <h1>Click on an event to RSVP or to read more information</h1>
      <br></br>

    </Jumbotron>

    <div style={{width: 800, height: 200, marginLeft: 350}}>
    <Calendar tileContent={tileContent, tileContento} showWeekNumbers onChange={onChange} value={date} />
    {date.toString()}
    </div>

  </div>
} ;

export default Events;
