import React from "react";
import emailjs from "emailjs-com";
// This file exports the Input, TextArea, and FormBtn components






export function Input(props) {
  return (
    <div class="control" style={{width: 500, marginLeft: 450}}>
      <input class="input is-primary" {...props} />
    </div>
  );
}

export function TextArea(props) {
  return (
    <div class="control" style={{width: 500, marginLeft: 450}}>
      <textarea class="input is-primary" rows="20" {...props} />
    </div>
  );
}

export function FormBtn(props) {
  emailjs.send('default_service', 'template_dhpghJPo', "user_Z1HryPA1jzVwmj7gx0knZ")
        .then(function(response) {
           console.log('SUCCESS!', response.status, response.text);
        }, function(error) {
           console.log('FAILED...', error);
        })
  return (
    <button {...props} style={{ marginBottom: 10 }} className="button is-primary">
      {props.children}

    </button>



  );
}
